          <div id="">
            <MDBInput value={this.state.value} onChange={this.onChange} name="prix" label="Prix" type="number" validate error="wrong" success="right" />
          </div>
          <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="photo1" /><br></br>
